commits.content:
@import "tailwindcss";

@theme {
  --font-sans: 'Inter', system-ui, -apple-system, sans-serif;
  --color-revolut-blue: #4B8BCB;
  --color-revolut-dark: #191C1F;
  --color-revolut-light: #F4F4F4;
  --color-revolut-gray: #8D969E;
  --color-revolut-card: #F7F7F7;
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: 'Inter', system-ui, -apple-system, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  color: #191C1F;
}

/* Selection */
::selection {
  background: #4B8BCB;
  color: #fff;
}

/* Revolut easing: cubic-bezier(0.35, 0, 0, 1) */
.r-ease {
  transition-timing-function: cubic-bezier(0.35, 0, 0, 1);
}

::-webkit-scrollbar {
  width: 6px;
}
::-webkit-scrollbar-track {
  background: transparent;
}
::-webkit-scrollbar-thumb {
  background: #c4c4c4;
  border-radius: 3px;
}

/* Card hover lift */
.card-lift {
  transition: transform 0.5s cubic-bezier(0.35, 0, 0, 1), box-shadow 0.5s cubic-bezier(0.35, 0, 0, 1);
}
.card-lift:hover {
  transform: translateY(-6px);
  box-shadow: 0 24px 48px rgba(0, 0, 0, 0.08);
}

img {
  transition: transform 0.8s cubic-bezier(0.35, 0, 0, 1), opacity 0.4s ease;
}

.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}
.line-clamp-3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* Smooth focus rings */
input:focus, button:focus-visible, a:focus-visible {
  outline: 2px solid #4B8BCB;
  outline-offset: 2px;
}

/* Prevent layout shift from images */
img {
  display: block;
  max-width: 100%;
  height: auto;
}
